import axios from 'axios';

const api = axios.create({
  baseURL: process.env.REACT_APP_API_URL || '/api',
  headers: {
    'Content-Type': 'application/json',
  },
});

export const setAuthToken = (token?: string | null) => {
  if (token) {
    api.defaults.headers.common.Authorization = `Bearer ${token}`;
  } else {
    delete api.defaults.headers.common.Authorization;
  }
};

const storedToken = localStorage.getItem('token');
if (storedToken) {
  setAuthToken(storedToken);
}

export const registerUser = async (userData: any) => {
  const response = await api.post('/auth/register', userData);
  return response.data;
};

export const loginUser = async (credentials: any) => {
  const response = await api.post('/auth/login', credentials);
  return response.data;
};

export const getCurrentUser = async () => {
  const response = await api.get('/auth/me');
  return response.data;
};

export const getSkills = async () => {
  const response = await api.get('/skills');
  return response.data;
};

export const startInterview = async (skillId: string) => {
  const response = await api.post(`/skills/${skillId}/interview/start`);
  return response.data;
};

export const getInterviewDetails = async (interviewId: string) => {
  const response = await api.get(`/skills/interviews/${interviewId}`);
  return response.data;
};

export const uploadInterviewVideo = async (
  skillId: string,
  interviewId: string,
  questionIndex: number,
  videoBlob: Blob
) => {
  const formData = new FormData();
  formData.append('video', videoBlob);
  formData.append('interviewId', interviewId);
  formData.append('questionIndex', String(questionIndex));

  const response = await api.post(`/skills/${skillId}/interview/upload`, formData, {
    headers: { 'Content-Type': 'multipart/form-data' },
  });
  return response.data;
};

export const postJob = async (jobData: any) => {
  const response = await api.post('/jobs', jobData);
  return response.data;
};

export const getJobs = async () => {
  const response = await api.get('/jobs');
  return response.data;
};

export const getClientJobs = async () => {
  const response = await api.get('/jobs/mine');
  return response.data;
};

export const submitProposal = async (jobId: string, proposalData: any) => {
  const response = await api.post(`/jobs/${jobId}/proposals`, proposalData);
  return response.data;
};

export const getJobProposals = async (jobId: string) => {
  const response = await api.get(`/jobs/${jobId}/proposals`);
  return response.data;
};

export const getClientProposals = async () => {
  const response = await api.get('/jobs/proposals/client');
  return response.data;
};

export const getMatchedCandidates = async (jobId: string) => {
  const response = await api.get(`/jobs/${jobId}/matches`);
  return response.data;
};

export const selectStudentForJob = async (jobId: string, studentId: string) => {
  const response = await api.post(`/jobs/${jobId}/select`, { studentId });
  return response.data;
};

export const getStudentProfile = async (studentId: string) => {
  const response = await api.get(`/students/${studentId}/profile`);
  return response.data;
};

export const updateStudentProfile = async (studentId: string, data: any) => {
  const response = await api.put(`/students/${studentId}/profile`, data);
  return response.data;
};

export const uploadStudentCV = async (studentId: string, file: File) => {
  const formData = new FormData();
  formData.append('cv', file);
  const response = await api.post(`/students/${studentId}/upload-cv`, formData, {
    headers: { 'Content-Type': 'multipart/form-data' },
  });
  return response.data;
};

export const getStudentProposals = async (studentId: string) => {
  const response = await api.get(`/students/${studentId}/proposals`);
  return response.data;
};

export const getInterviews = async () => {
  const response = await api.get('/admin/interviews');
  return response.data;
};

export const reviewInterview = async (interviewId: string, payload: any) => {
  const response = await api.post(`/admin/review/${interviewId}`, payload);
  return response.data;
};

export const getInterviewById = async (interviewId: string) => {
  const response = await api.get(`/admin/interviews/${interviewId}`);
  return response.data;
};

export const getAdminUsers = async () => {
  const response = await api.get('/admin/users');
  return response.data;
};

export const deleteAdminUser = async (userId: string) => {
  const response = await api.delete(`/admin/users/${userId}`);
  return response.data;
};

export const getAdminJobs = async () => {
  const response = await api.get('/admin/jobs');
  return response.data;
};

export const deleteAdminJob = async (jobId: string) => {
  const response = await api.delete(`/admin/jobs/${jobId}`);
  return response.data;
};

export const fetchJobs = getJobs;
export const getJobListings = getJobs;
export const uploadVideo = uploadInterviewVideo;
export const getProposals = getClientProposals;

export default api;
