import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import { getSkills, startInterview } from '../../services/api';

const SkillVerification: React.FC = () => {
    const history = useHistory();
    const [skills, setSkills] = useState<any[]>([]);

    useEffect(() => {
        // Fetch available skills from the backend
        const fetchSkills = async () => {
            try {
                const response = await getSkills();
                setSkills(response);
            } catch (error) {
                console.error('Error fetching skills:', error);
            }
        };

        fetchSkills();
    }, []);

    const handleStartInterview = async (skillId: string) => {
        try {
            const response = await startInterview(skillId);
            history.push(`/student/video-interview/${response.interviewId}`);
        } catch (error) {
            console.error('Error starting interview:', error);
        }
    };

    return (
        <div>
            <h2>Skill Verification</h2>
            <h3>Select a Skill to Verify:</h3>
            {skills.length === 0 ? (
                <p>No skills available.</p>
            ) : (
                <ul>
                    {skills.map((skill) => (
                        <li key={skill._id}>
                            <button onClick={() => handleStartInterview(skill._id)}>{skill.name}</button>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default SkillVerification;
