import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import { getSkills, postJob } from '../../services/api';

const PostJob: React.FC = () => {
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [skills, setSkills] = useState<string[]>([]);
    const [availableSkills, setAvailableSkills] = useState<any[]>([]);
    const history = useHistory();

    useEffect(() => {
        const fetchSkills = async () => {
            try {
                const response = await getSkills();
                setAvailableSkills(response);
            } catch (error) {
                console.error('Error fetching skills:', error);
            }
        };

        fetchSkills();
    }, []);

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        try {
            await postJob({ title, description, requiredSkills: skills });
            history.push('/client/dashboard'); // Redirect to client dashboard after posting
        } catch (error) {
            console.error('Error posting job:', error);
        }
    };

    const handleSkillChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
        const value = e.target.value;
        setSkills(prevSkills => 
            prevSkills.includes(value) 
                ? prevSkills.filter(skill => skill !== value) 
                : [...prevSkills, value]
        );
    };

    return (
        <div className="post-job">
            <h2>Post a New Job</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="title">Job Title:</label>
                    <input 
                        type="text" 
                        id="title" 
                        value={title} 
                        onChange={(e) => setTitle(e.target.value)} 
                        required 
                    />
                </div>
                <div>
                    <label htmlFor="description">Job Description:</label>
                    <textarea 
                        id="description" 
                        value={description} 
                        onChange={(e) => setDescription(e.target.value)} 
                        required 
                    />
                </div>
                <div>
                    <label htmlFor="skills">Required Skills:</label>
                    {availableSkills.length === 0 ? (
                        <p>No skills available.</p>
                    ) : (
                        <select id="skills" multiple onChange={handleSkillChange}>
                            {availableSkills.map((skill) => (
                                <option key={skill._id} value={skill._id}>{skill.name}</option>
                            ))}
                        </select>
                    )}
                </div>
                <button type="submit">Post Job</button>
            </form>
        </div>
    );
};

export default PostJob;
