import React, { useEffect, useState } from 'react';
import useAuth from '../../hooks/useAuth';
import api from '../../services/api';
import './StudentProfile.css';

const StudentProfile: React.FC = () => {
    const { user } = useAuth();
    const [profile, setProfile] = useState<any>(null);
    const [loading, setLoading] = useState<boolean>(true);
    const [error, setError] = useState<string | null>(null);

    const fetchProfile = async () => {
        try {
            const response = await api.get(`/students/${user._id || user.id}/profile`);
            setProfile(response.data);
        } catch (err) {
            setError('Failed to load profile');
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        if (user) {
            fetchProfile();
        }
    }, [user]);

    const handleCVUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
        const file = event.target.files?.[0];
        if (file) {
            const formData = new FormData();
            formData.append('cv', file);

            try {
                await api.post(`/students/${user._id || user.id}/upload-cv`, formData);
                // Optionally refresh profile after upload
                fetchProfile();
            } catch (err) {
                setError('Failed to upload CV');
            }
        }
    };

    if (!user || loading) return <div>Loading...</div>;
    if (error) return <div>{error}</div>;

    return (
        <div className="student-profile">
            <h2>{profile.name}'s Profile</h2>
            <p>Email: {profile.email}</p>
            <h3>Verified Skills</h3>
            <ul>
                {profile.verifiedSkills?.map((skill: any) => (
                    <li key={skill.skill?._id || skill.skill?.name || skill._id}>
                        {skill.skill?.name || skill.name} - Score: {skill.score}
                    </li>
                ))}
            </ul>
            {profile.verifiedSkills?.length === 0 && <p>No verified skills yet.</p>}
            {profile.cvUrl && (
                <p>
                    CV: <a href={profile.cvUrl} target="_blank" rel="noopener noreferrer">View</a>
                </p>
            )}
            <h3>Upload CV</h3>
            <input type="file" accept=".pdf,.doc,.docx" onChange={handleCVUpload} />
        </div>
    );
};

export default StudentProfile;
