import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { getClientJobs, getClientProposals } from '../../services/api';
import { Job, Proposal } from '../../types';

const ClientDashboard: React.FC = () => {
    const [jobs, setJobs] = useState<Job[]>([]);
    const [proposals, setProposals] = useState<Proposal[]>([]);

    useEffect(() => {
        // Fetch jobs posted by the client
        const fetchJobs = async () => {
            const fetchedJobs = await getClientJobs();
            setJobs(fetchedJobs);
        };

        // Fetch proposals submitted by students for the client's jobs
        const fetchProposals = async () => {
            const fetchedProposals = await getClientProposals();
            setProposals(fetchedProposals);
        };

        fetchJobs();
        fetchProposals();
    }, []);

    return (
        <div className="client-dashboard">
            <h1>Client Dashboard</h1>
            <h2>Posted Jobs</h2>
            <ul>
                {jobs.map(job => (
                    <li key={job._id || job.id}>
                        <Link to={`/client/view-proposals`}>{job.title}</Link>
                    </li>
                ))}
            </ul>

            <h2>Proposals</h2>
            {proposals.length === 0 ? (
                <p>No proposals yet.</p>
            ) : (
                <ul>
                    {proposals.map((proposal: any) => (
                        <li key={proposal._id || proposal.id}>
                            <strong>{proposal.studentId?.name || 'Student'}</strong>: {proposal.details} - Status: {proposal.status}
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default ClientDashboard;
