import React, { useEffect, useState } from 'react';
import { getClientJobs, getProposals, getSkills, selectStudentForJob } from '../../services/api';
import { Proposal } from '../../types';

const ViewProposals: React.FC = () => {
    const [proposals, setProposals] = useState<Proposal[]>([]);
    const [skills, setSkills] = useState<any[]>([]);
    const [jobs, setJobs] = useState<any[]>([]);
    const [selectedJob, setSelectedJob] = useState<string>('');
    const [selectedSkill, setSelectedSkill] = useState<string>('');
    const [loading, setLoading] = useState<boolean>(true);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        const fetchProposals = async () => {
            try {
                const response = await getProposals();
                setProposals(response);
                const skillsResponse = await getSkills();
                setSkills(skillsResponse);
                const jobsResponse = await getClientJobs();
                setJobs(jobsResponse);
            } catch (err) {
                setError('Failed to fetch proposals');
            } finally {
                setLoading(false);
            }
        };

        fetchProposals();
    }, []);

    if (loading) {
        return <div>Loading proposals...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    const filteredProposals = proposals.filter((proposal: any) => {
        if (selectedJob && proposal.jobId?._id !== selectedJob) {
            return false;
        }
        if (!selectedSkill) {
            return true;
        }
        return proposal.studentId?.verifiedSkills?.some(
            (verified: any) => (verified.skill?._id || verified.skill) === selectedSkill
        );
    });

    const handleSelectStudent = async (jobId: string, studentId: string) => {
        try {
            if (!jobId || !studentId) {
                return;
            }
            await selectStudentForJob(jobId, studentId);
            setProposals(prev =>
                prev.map((proposal: any) =>
                    proposal.jobId?._id === jobId
                        ? { ...proposal, status: proposal.studentId?._id === studentId ? 'accepted' : 'rejected' }
                        : proposal
                )
            );
        } catch (err) {
            setError('Failed to select student');
        }
    };

    return (
        <div>
            <h2>Submitted Proposals</h2>
            <div>
                <label htmlFor="jobFilter">Filter by Job:</label>
                <select
                    id="jobFilter"
                    value={selectedJob}
                    onChange={(e) => setSelectedJob(e.target.value)}
                >
                    <option value="">All Jobs</option>
                    {jobs.map((job) => (
                        <option key={job._id} value={job._id}>{job.title}</option>
                    ))}
                </select>
            </div>
            <div>
                <label htmlFor="skillFilter">Filter by Verified Skill:</label>
                <select
                    id="skillFilter"
                    value={selectedSkill}
                    onChange={(e) => setSelectedSkill(e.target.value)}
                >
                    <option value="">All Skills</option>
                    {skills.map((skill) => (
                        <option key={skill._id} value={skill._id}>{skill.name}</option>
                    ))}
                </select>
            </div>
            {filteredProposals.length === 0 ? (
                <p>No proposals submitted yet.</p>
            ) : (
                <ul>
                    {filteredProposals.map((proposal: any) => (
                        <li key={proposal._id || proposal.id}>
                            <h3>{proposal.jobId?.title || 'Job'}</h3>
                            <p>{proposal.studentId?.name || 'Student'}</p>
                            <p>{proposal.details}</p>
                            <p>Status: {proposal.status}</p>
                            <p>
                                Verified Skills:{' '}
                                {(proposal.studentId?.verifiedSkills || [])
                                    .map((verified: any) => verified.skill?.name || verified.skill)
                                    .filter(Boolean)
                                    .join(', ') || 'None'}
                            </p>
                            {proposal.status === 'pending' && proposal.jobId?.status !== 'filled' && (
                                <button
                                    type="button"
                                    onClick={() => handleSelectStudent(proposal.jobId?._id, proposal.studentId?._id)}
                                >
                                    Select Student
                                </button>
                            )}
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default ViewProposals;
