import React, { useEffect, useState } from 'react';
import { useHistory, useParams } from 'react-router-dom';
import { getInterviewById, reviewInterview } from '../../services/api';

const ReviewInterview: React.FC = () => {
    const { interviewId } = useParams<{ interviewId: string }>();
    const [interview, setInterview] = useState<any>(null);
    const [score, setScore] = useState<number>(0);
    const [status, setStatus] = useState<string>('pass'); // 'pass' or 'fail'
    const [loading, setLoading] = useState<boolean>(true);
    const history = useHistory();

    useEffect(() => {
        const fetchInterviewDetails = async () => {
            try {
                const response = await getInterviewById(interviewId);
                setInterview(response);
            } catch (error) {
                console.error('Error fetching interview details:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchInterviewDetails();
    }, [interviewId]);

    const handleReview = async () => {
        try {
            await reviewInterview(interviewId, { score, status });
            alert('Interview reviewed successfully!');
            history.push('/admin/dashboard');
        } catch (error) {
            console.error('Error reviewing interview:', error);
        }
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    if (!interview) {
        return <div>No interview found.</div>;
    }

    return (
        <div>
            <h2>Review Interview for {interview.studentId?.name || 'Student'}</h2>
            <p>Skill: {interview.skillId?.name || interview.skillId}</p>
            {interview.responses?.map((response: any, index: number) => (
                <div key={index}>
                    <p>Question {index + 1}: {response.question}</p>
                    <video controls src={response.videoUrl} />
                </div>
            ))}
            <div>
                <label>
                    Score (0-100):
                    <input
                        type="number"
                        value={score}
                        onChange={(e) => setScore(Number(e.target.value))}
                        min="0"
                        max="100"
                    />
                </label>
            </div>
            <div>
                <label>
                    Status:
                    <select value={status} onChange={(e) => setStatus(e.target.value)}>
                        <option value="">Select</option>
                        <option value="pass">Pass</option>
                        <option value="fail">Fail</option>
                    </select>
                </label>
            </div>
            <button onClick={handleReview}>Submit Review</button>
        </div>
    );
};

export default ReviewInterview;
